import * as R from 'ramda'

import {
    ADD_PHONE_TO_BASKET,
    REMOVE_FROM_BASKET,
    CLEAR_BASKET
} from 'actionTypes'

const initialState = []

export default (state = initialState, action) => {
    switch (action.type) {
        case ADD_PHONE_TO_BASKET:
            return R.append(action.payload, state)
        case REMOVE_FROM_BASKET:
            return R.without(R.of(action.payload), state)
        case CLEAR_BASKET: 
            return []    
        default:
            return state
    }
}