import * as R from 'ramda'

import {
    FETCH_CATIGORIES_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, action) => {
    switch(action.type) {
        case FETCH_CATIGORIES_SUCCESS:
            const newValues = R.indexBy(R.prop('id'), action.payload)
            return R.merge(state, newValues)
        default:
            return state
    }
}